package ejercicio.java;

public class index {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x=144;
    int y=999;
    int suma;
    int resta;
    double multi;
    double div=(double)x/y;
    
    System.out.println("x=144");
    System.out.println("y=999");
    suma=x+y;
    System.out.print(" la suma entre x e y es:");
    System.out.println(suma);
    
    resta=x-y;
    System.out.print(" la resta entre x e y es:");
    System.out.println(resta);
    
    multi=x*y;
    System.out.print(" la multiplicacion entre x e y es:");
    System.out.println(multi);
    

    System.out.print(" la division entre x e y es:");
    System.out.println(div);
     
 	}

}
